css:
.platformio-ide-terminal {
  letter-spacing: normal;
  position: inherit;
}
.platformio-ide-terminal ul {
  padding: 0;
  margin: 0;
}
.platformio-ide-terminal .icon::before {
  margin: 0;
  padding: 0;
}
.platformio-ide-terminal .icon-x::before {
  content: '\f081' !important;
  line-height: normal;
}
.platformio-ide-terminal.terminal-view .panel-divider {
  order: 1;
  display: inherit;
  flex: 0 0 auto;
  position: absolute;
  height: 8px;
  margin-top: -4px;
  z-index: 3;
  -webkit-user-select: none;
  cursor: ns-resize;
  width: 100%;
}
.platformio-ide-terminal.terminal-view .input-block {
  padding: 0.375em;
}
.platformio-ide-terminal.terminal-view .input-block .btn-toolbar {
  padding: 0.375em;
}
.platformio-ide-terminal.terminal-view .input-block .btn-toolbar .left {
  float: left;
}
.platformio-ide-terminal.terminal-view .input-block .btn-toolbar .right {
  float: right;
}
.platformio-ide-terminal.terminal-view .xterm {
  display: -webkit-flex;
  order: 3;
  flex: 1;
  width: 100%;
  height: 0px;
  padding: 0 5px;
}
.platformio-ide-terminal.terminal-view .xterm > .terminal {
  display: -webkit-flex;
  flex-direction: column;
  align-content: flex-end;
  position: relative;
  width: 100%;
  overflow: hidden;
}
.platformio-ide-terminal.terminal-view .xterm > .terminal > div {
  flex: 1 0 auto;
  white-space: nowrap;
  font-size: inherit;
}
.platformio-ide-terminal.terminal-view .xterm > .terminal > div > span {
  display: inline-block;
  line-height: inherit;
  height: inherit;
}
.platformio-ide-terminal.terminal-view .cursor-blink .terminal-cursor {
  -webkit-animation: blink 1s step-start 0s infinite;
  -webkit-animation-delay: 1s;
}
@-webkit-keyframes blink {
  50% {
    background: transparent;
  }
}
.platformio-ide-terminal.status-bar {
  position: relative;
  vertical-align: middle;
  display: inline;
  -webkit-user-select: none;
  margin: 0 10px;
  box-sizing: content-box;
  height: 20px;
}
.platformio-ide-terminal.status-bar i {
  cursor: pointer;
  display: inherit;
  text-align: center;
  align-self: center;
  line-height: 20px;
}
.platformio-ide-terminal.status-bar .icon-x {
  color: #9da5b4;
  justify-content: flex-end;
}
.platformio-ide-terminal.status-bar .status-container {
  display: inherit;
  position: relative;
  width: 100%;
  height: 100%;
  line-height: 20px;
  margin: 0 5px;
}
.platformio-ide-terminal.status-bar .status-container .pio-terminal-status-icon {
  display: inherit;
  cursor: pointer;
  -webkit-transition: opacity 0.25s ease-in-out, color 0.5s ease-in-out;
  -webkit-user-drag: element;
  padding: 0 5px;
  opacity: 0.5;
  height: 100%;
}
.platformio-ide-terminal.status-bar .status-container .pio-terminal-status-icon.active {
  opacity: 1;
}
.platformio-ide-terminal.status-bar .status-container .is-dragging {
  border-bottom: 1px solid #0f82e6;
}
.platformio-ide-terminal.status-bar .status-container .placeholder {
  pointer-events: none;
  position: absolute;
  top: -16px;
  color: #0f82e6;
  z-index: 999;
  margin: 0 -6px;
  padding: 0;
  height: 10px;
  line-height: 10px;
}
.platformio-ide-terminal.status-bar .status-container .placeholder::before {
  font-size: 16px;
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  content: "\f05b";
}
.platformio-ide-terminal .inserted {
  -webkit-animation: flash 0.25s linear 6 alternate;
}
@-webkit-keyframes flash {
  0% {
    opacity: 0.5;
  }
  100% {
    opacity: 1;
  }
}
